import React from "react";

export const renderField = ({
                                input,
                                meta: { touched, error, warning }
                            }) => {

    return    <div>
        <textarea {...input}  />
        {touched &&
            ((error && <span>{error}</span>) ||
                (warning && <span>{warning}</span>))}
    </div>
}
