import React from 'react';
import { connect } from 'react-redux';
import MyPost from "./MyPost";
import {ActionCreatorFollow, ActionCreatorSetUsers, ActionCreatorUnFollow} from "../../../Readux/Reducer-User";
import User from "../../User/User";

let myPostToprops = (state)=>{
    return{
        users: state.users

    }
}
let myPostToDispatch = (dispatch)=>{
    return{
        Follow: (userid)=>{
            dispatch(ActionCreatorFollow(userid))
        },
        onFollow: (userid)=>{
            dispatch(ActionCreatorUnFollow(userid))
        },
        SetUsers: (users)=>{
            dispatch(ActionCreatorSetUsers(users))
        }
    }

}
 const UserContainer = connect(myPostToprops,myPostToDispatch)(User)
export default UserContainer
