import React from 'react';

export default class ProfileStratus extends React.Component {

    state = {
        status: this.props.profilestatus,
        editmode: false
    }
    activatedStatusMode = () => {
        this.setState({
            editmode: true
        })
    }
    deactivateStatusMode = () => {
        this.setState({
            editmode: false
        })
        this.props.UpdateProfileThunkStatus(this.state.status)
    }
    onStatusChange = (e) => {
        console.log(e.currentTarget.value)
        this.setState({
            status: e.currentTarget.value
        })
    }

    componentDidUpdate(PrevProps) {
console.log('componentDidUpdate')
        if (PrevProps.profilestatus != this.props.profilestatus) {
            this.setState({
                status: this.props.profilestatus
            })
        }
    }

    render() {
        console.log('render')
        return <div>
            {!this.state.editmode && <div><span
                onClick={this.activatedStatusMode}>{this.state.status || '--------' }</span>
            </div>}
            {this.state.editmode &&
                <div><input autoFocus={true}  onBlur={this.deactivateStatusMode} defaultValue={this.props.profilestatus}
                            value={this.props.profilestatus} onChange={this.onStatusChange} type="text"/></div>}
        </div>
    }

}
