import React from 'react'
import s from './../Dialogs.module.css'
import {NavLink} from "react-router-dom";
const DialogItem = (props) => {
    let refmessage = React.createRef()
    let addMessage =()=>{
     text = refmessage.current.value
        alert (text)
    }
    return (

        <div className={s.dialog + ' ' + s.active}>
            <NavLink className={navData => navData.isActive ? s.active : s.dialog}
                     to={'/dialogs/' + props.id}> {props.name}</NavLink>
            <textarea name="" ref={refmessage} cols="30" rows="10"></textarea>
            <button onClick={addMessage}></button>
        </div>
    )
}

export default DialogItem
