import React from 'react';

class ProfileStatus extends React.Component {
    state = {
        editmode: true
    }

    render() {
        return <div>
            {!this.state.editmode &&
                <div><span>{this.props.status}</span></div>
            }
            {this.state.editmode &&
                <div>
                    <input type="text" value={this.props.status}/>
                </div>
            }
        </div>
    }
}

export default ProfileStatus;
