import {LoginAPI, usersAPI as loginAPI, usersAPI} from "../api/api";
import {AuthThunk} from "./Reducer-Auth";

const SET_INITIALIZE_APP = 'SET_INITIALIZE_APP'


let initstate = {
    initializet: false
}
const reducerApp = (state = initstate, action) => {
    switch (action.type) {
        case SET_INITIALIZE_APP:
           return {
               ...state,
               initializet: true
           }
        default:
            return state
    }
}
export const SetInitialize = () => ({type: SET_INITIALIZE_APP})
export const InitializetThunk = ()=>(dispatch)=> {
  let dispatchRezult =  dispatch(AuthThunk())
    
   dispatch(SetInitialize)
}


export default reducerApp
