import React from 'react';
import s from './FormControls.module.css'

export const FormControl = ({input,child,element, meta, ...props}) => {
    const haserror = meta.touched && meta.error
    return (
        <div className={s.FormControls + ' ' + (haserror ? s.error : ' ')}>
            <div>
                <element {...props} />
            </div>
            <div>
                {haserror && <span>{meta.error}</span>}
            </div>
        </div>
    )
}
export const Textarea = ({input, meta, ...props}) => {
       return <FormControl  {...props}> element= {'textarea'}</FormControl>
}
export const Input = ({input, meta, ...props}) => {
      return  <FormControl  {...props}> element= {'input'}</FormControl>
}
