import React from 'react';
import axios from "axios";


let User = (props)=>{
    console.log(props.users == 0 )
    let SetUsers = ()=>{


            if(props.users == 0){

            axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response=>{
                console.log(response.data.items)

                props.SetUsers(response.data.items)
            })
        }

    }

    return <div>
        <button onClick={SetUsers}>SetUsers</button>
        {
props.users.map(u=>(
    <div>
        <div>{u.name}</div>
        {u.followed ===true ? <button onClick={()=>props.onFollow(u.id)}>unfollow</button> : <button onClick={()=>props.Follow(u.id)}>follow</button> }
    </div>
))
        }
    </div>
}
export default User
