import React from 'react';


import {ActionCreatorAddPost, ActionCreatorUpdatePostText} from "../../../Readux/Reducer-Profile";
import MyPost from "./MyPost";

const MyPostContainer = (props) => {

//let state = props.getState()
   let posts = props.state.ProfilePage.PostData
    console.log('props')
    console.log(posts)
    let onaddPost = () => {
        props.dispatch(ActionCreatorAddPost())
    }
    let OnPostChange = (e) => {
        let text = e.target.value
        let newVar = ActionCreatorUpdatePostText(text)
        props.dispatch(newVar)
    }
    return (
        <MyPost posts={posts} addPost={onaddPost} OnPostChange={OnPostChange}  />
    );
}
export default MyPostContainer;
