    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    withCredentials: true,
       headers: {
        'API-KEY' : '86c79010-dccf-4af0-abdb-c44354c00af2'
    }
})

 export const profileAPI = {

     getProfile (userid=3)  {
         return     instance.get(`profile/${userid}`)

     }
 }
