import React from "react";
import s from './FormStyle.module.css'

 const FormControls = ({input,meta,child,...props }) => {
    return (
        <div >
            {props.child}
            {meta.touched &&
                ((meta.error && <span className={s.error}>{meta.error}</span>))}
        </div>
    )
}

export const renderField = (props)=>{
    return <FormControls {...props}><textarea {...props.input} {...props}  /> </FormControls>
}
export const renderFieldDialog = ({
                                      input,
                                      meta: {touched, error}
                                  }) => {
    return (
        <div>
            <textarea className={error ? s.formControls : ''} {...input}  />
            {touched &&
                ((error && <span className={s.error}>{error}</span>))}
        </div>
    )
}

export const renderFieldLoginEmail = ({
                                      input,
                                      meta: {touched, error}
                                  }) => {
    return (
        <div>
            <input placeholder='enter email' className={error ? s.formControls : ''} {...input}  />
            {touched &&
                ((error && <span className={s.error}>{error}</span>))}
        </div>
    )
}
export const renderFieldLoginPassword = ({
                                      input,
                                      meta: {touched, error}
                                  }) => {
    return (
        <div>
            <input placeholder='enter password' className={error ? s.formControls : ''} {...input}  />
            {touched &&
                ((error && <span className={s.error}>{error}</span>))}
        </div>
    )
}
