import React from 'react'
import s from "./UsersItem/UsersItem.module.css";
import {UnFollowUserAC} from "../../Readux/Reducer-Users";
const Users = (props) => {
    let unfollow = (e)=>{
        let userf = e.target.id
        console.log(userf)
        //props.UnFollow(userf)
    }
    let follow = ()=>{
        alert('vsvsv')
    }
    return <div >

        {
            props.Users.map(u=><div key={u.id}  >
                <div className={s.main}>
                    <div className={s.users}>
                        <img className={s.usersPhoto} src="https://ru.reactjs.org/logo-og.png" alt=""/>
                        <div>
                            {u.followed ? <button  onClick={()=>{props.UnFollow(u.id)}} >unfollow</button> : <button onClick={follow} >follow</button>}
                        </div>
                    </div>
                    <div className={s.item}>
                        <div className={s.one}>{u.name}</div>
                        <div className={s.two}>{u.location.country}</div>
                        <div className={s.three}>{u.message}</div>
                        <div className={s.foor}> {u.location.city}</div>
                    </div>
                </div>
            </div>   )
        }
                    </div>


}
export default Users
