import React from 'react';

export default class ProfileStratus extends React.Component {

    state = {
        status: this.props.profilestatus,
        editmode: false
    }
    activatedStatusMode = () => {
        this.setState({
            editmode: true
        })
    }
    deactivateStatusMode = (event) => {
        this.setState({
            editmode: false
        })
        this.props.UpdateProfileThunkStatus(event.target.value)
    }
    addLocalStatus = (event) => {
        this.props.UpdateProfileThunkStatus(event.target.value)
    }

    componentDidUpdate(PrevProps) {

        if (PrevProps.profilestatus != this.props.profilestatus) {
            this.setState({
                status: this.props.profilestatus
            })
        }
    }

    render() {
///console.log(this.state.status)
        return <div>
            {!this.state.editmode && <div><span
                onClick={this.activatedStatusMode}>{this.state.status === null ? 'нет статуса' : this.state.status}</span>
            </div>}
            {this.state.editmode &&
                <div><input autoFocus={true}  onBlur={this.deactivateStatusMode}
                            value={this.props.profilestatus} type="text"/></div>}
        </div>
    }

}
