import React from 'react';
import s from './FormControls.module.css'

export const FormControl = ({input,child, meta, ...props}) => {
    const haserror = meta.touched && meta.error
    return (
        <div className={s.FormControls + ' ' + (haserror ? s.error : ' ')}>
            <div><textarea {...props} {...input} />
            </div>
            <div>
                {haserror && <span>{meta.error}</span>}
            </div>
        </div>
    )
}
export const Textarea = ({input, meta, ...props}) => {
       return <FormControl  {...props}> <textarea {...props} {...input} /></FormControl>
}
export const Input = ({input, meta, ...props}) => {
      return  <FormControl  {...props}> <input {...props} {...input} /></FormControl>
}
