import React from "react";
import {Navigate} from "react-router-dom";
import {connect} from "react-redux";
let mapStateToRedirectProps=(state)=>{
    return{
        isAuth: state.Auth.isAuth
    }
}

let hocRedirect=(Component)=> {
    class RedirectContainer extends React.Component {
        render() {
            if (!props.isAuth) return <Navigate to={'/login'}/>
            return <Component {...props} />
        }
    }
    return RedirectContainer
}


let hocRedirectConnect = connect(mapStateToRedirectProps)(hocRedirect)
export default hocRedirectConnect
