import React from "react";
import s from 'src/Component/FormControls/FormStyle.module.css'
export const renderField = ({
                                input,
                                meta: { touched, error, warning }
                            }) => {

    return(
    <div>
        <textarea className={s.formssssss } {...input}  />
        {touched &&
            ((error && <span>{error}</span>) ||
                (warning && <span>{warning}</span>))}
    </div>
    )
}
