import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import store from './Readux/State'
//import {subscribe} from "./Readux/State";
let render = ()=> {
    ReactDOM.render(
        <App state={store.getState()} addPost={store.addPost.bind(store)} PostTextChange={store.PostTextChange.bind(store)}/>,
        document.getElementById('root')
    )
}

