import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import store from './Readux/State'
//import {subscribe} from "./Readux/State";
  let rerenderState = (store) =>{
 ReactDOM.render(
     <App state={store.state} addPost={store.addPost} PostTextChange={store.PostTextChange} />,
     document.getElementById('root')
 )
}
rerenderState(store)
store.setState(rerenderState)
store.getState()
console.log(store)
console.log(store.subscribe(rerenderState))

