import {LoginAPI, usersAPI as loginAPI, usersAPI} from "../api/api";
import {stopSubmit} from "redux-form";

const SETAUTH = 'SETAUTH'
const DELETE_SETAUTH = 'DELETE_SETAUTH'
const SET_ERROR = 'SET_ERROR'

let initstate = {
    userid: '',
    login: '',
    email: '',
    isAuth: false,
    errors: ''
}
const reducerAuth = (state = initstate, action) => {
    switch (action.type) {
        case SETAUTH:
            return {
                ...state,
                ...action.payload
            }
        case SET_ERROR:
            return{
                ...state,
                errors: action.error
            }
        case DELETE_SETAUTH:
            return {
                ...state,
                ...action.payload
            }
        default:
            return state
    }
}
export const SetAuth = (id, login, email, isAuth) => ({type: SETAUTH, payload: {id, login, email, isAuth}})
export const adderrors = (error)=>({type: SET_ERROR,error:error})
export const AuthThunk = (dispatch) => async (dispatch) => {
    let response = await usersAPI.apiAuth()
    if (response.data.resultCode === 0) {
        let {id, login, email} = response.data.data
        dispatch(SetAuth(id, login, email, true))
    }

}
export const LoginThunk = (email, password, rememberMe) => async (dispatch) => {
    //let action = stopSubmit('login',{email: 'uncorrect login'})
    let action = stopSubmit('login',{email: 'uncorrect login',password: 'uncorrect password'})
    dispatch(action)
    let response = await LoginAPI.authorize(email, password, rememberMe)
    if (response.data.resultCode === 0) {
        dispatch(AuthThunk())
    }else {dispatch(adderrors(response.data.messages[0]))}
}
export const deleteLogin = () => async (dispatch) => {
    let response = await LoginAPI.deleteauthorize()
    if (response.data.resultCode === 0) {
        dispatch(SetAuth(null, null, null, false))
    }
}

export default reducerAuth
