import React from 'react';
import {connect} from 'react-redux';
import {
    OnFollow,
    UnFollow,
    SetUsers,
    SetPage,
    Togle,
    TogleFollow,
    ThunkCreatorGetUsers, ThunkCreatorDeleteUsers, ThunkCreatorFollowUsers
} from '../../Readux/Reducer-Users';
import axios from 'axios';
import Users from './Users';
import Preloader from "../Common/Preloader";
import {UserApi} from "../../api/api";

class UserContainer extends React.Component {


    componentDidMount() {
        this.props.ThunkCreatorGetUsers(this.props.currentPage, this.props.pagesize)
        //this.props.Togle(true)
        //UserApi.getUserApi(this.props.currentPage, this.props.pagesize).then(data => {
         //   this.props.SetUsers(data.items)
          //  this.props.Togle(false)
        //})
    }

    onPageChange = (p) => {
        this.props.ThunkCreatorGetUsers(p, this.props.pagesize)

    }


    render() {
        return <>
            {
                this.props.isfetching ? <Preloader/> : null

            }
            <Users onPageChange={this.onPageChange}
                   Users={this.props.Users}
                   TotalCount={this.props.TotalCount}
                   pagesize={this.props.pagesize}
                   UnFollow={this.props.UnFollow}
                   OnFollow={this.props.OnFollow}
                   currentPage={this.props.currentPage}
                   togglefollow={this.props.togglefollow}
                   TogleFollow={this.props.TogleFollow}
                   ThunkCreatorDeleteUsers={this.props.ThunkCreatorDeleteUsers}
                   ThunkCreatorFollowUsers={this.props.ThunkCreatorFollowUsers}
            />
        </>
    }
}

let mapStateToUsers = (state) => {
    return {
        Users: state.usersPage.users,
        TotalCount: state.usersPage.TotalCount,
        pagesize: state.usersPage.PageSize,
        currentPage: state.usersPage.currentPage,
        isfetching: state.usersPage.isfetching,
        togglefollow: state.usersPage.togglefollow
    }
}


export default connect(mapStateToUsers, {OnFollow, UnFollow,   TogleFollow,ThunkCreatorGetUsers,ThunkCreatorDeleteUsers,ThunkCreatorFollowUsers})(UserContainer)
