import React from 'react';
import s from './FormControls.module.css'
import {Field} from "redux-form";

export const FormControl = ({input, child, meta:{touched,error}, ...props}) => {
    const haserror = touched && error
    console.log(error)
    return (
        <div className={s.FormControls + ' ' + (haserror ? s.error : ' ')}>
            <div>
                {props.children}
            </div>
            <div>
                {haserror && <span>{error}</span>}
            </div>
        </div>
    )
}
export const Textarea = (props) => {
    const {input, child, meta, ...restProps} = props
    return <FormControl  {...props}> <textarea {...restProps} {...input} /></FormControl>
}
export const Input = (props) => {
    const {input, child, meta, ...restProps} = props
    return <FormControl  {...props}> <input {...restProps} {...input} /></FormControl>
}
export const createField = (type, name, component, validate, placeholder,props={},text={}) => (
    <div><Field type={type} name={name} component={Input} validate={validate}
                placeholder={placeholder} {...props} />{text}
    </div>
)
