const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SETUSERS = 'SETUSERS'

let initstate = {
    users: [


    ]}
const reducerUser = (state = initstate, action) => {
    switch(action.type){
        case FOLLOW:{
            return {
                ...state,
                users: state.users.map(u=>{
                    if(u.id === action.userid){
                        return {...u, followed : true}
                    }
                    return u
                })
            }
        }
        case UNFOLLOW:{
            return {
                ...state,
                users: state.users.map(u=>{
                    if(u.id === action.userid){
                        return {...u, followed : false}
                    }
                    return u
                })
            }
        }
        case SETUSERS:{

            return{...state.users,...action.users}
        }
        default:
            return state
    }
}

export const ActionCreatorFollow = (userid) => ({type: FOLLOW, userid})
export const ActionCreatorUnFollow = (userid) => ({type: UNFOLLOW, userid})
export const ActionCreatorSetUsers = (users) => ({type: SETUSERS, users})
export default reducerUser
