import React from 'react'
import {Field, reduxForm} from "redux-form";
import {Navigate} from 'react-router-dom'
import {connect} from "react-redux";
import {LoginThunk} from "../../Readux/Reducer-Auth";
const FormLogin = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <div>
                <Field type={'input'} component='input' name='email' placeholder='text'/>
                </div>
                    <div>
                <Field type={'input'} component='input' name='password' placeholder='text'/>
                    </div><div>
                <Field type={'input'} component='checkbox' name='rememberme' />
                    </div>
                        <div>
                    <button>Отправить</button>
                </div>
            </div>
        </form>
    )
}
const LoginFormRedux = reduxForm({
    form: 'simple' // a unique identifier for this form
})(FormLogin)
const Loginpage = (props)=>{
    console.log(props)
const onSubmit=(formdata)=>{
    console.log(formdata)
        LoginThunk(formdata.email,formdata.passoword,formdata.rememberme)
}
if (props.Auth)
{
    return <Navigate to={'/profile'} />
}
    return <div>
        <LoginFormRedux onSubmit={onSubmit} />
    </div>
}

let mapStateToPros= (state)=>{
    return {
        isAuth: state.Auth.isAuth
    }
}
export default connect(mapStateToPros,LoginThunk)(Loginpage)
