import React from 'react';
export default class ProfileStratus extends React.Component{
    state={
        editmode: false
    }
    activatedStatusMode=()=>{
    this.setState({
                 editmode: true
             })
}
deactivateStatusMode=()=>{
        this.setState({
            editmode: false
        })
}
        render(){
        return <div>
            {!this.state.editmode && <div><span onClick={this.activatedStatusMode}>casdads</span> </div>   }
            {this.state.editmode && <div><input autoFocus={true} onBlur={this.deactivateStatusMode} type="text"/> </div>   }
        </div>
        }

}
