import React from 'react';

import {ActionCreatorAddPost, ActionCreatorUpdatePostText} from "../../../Readux/Reducer-Profile";
import MyPost from "./MyPost";
import {connect} from "react-redux";
var a = {
    name: 'TI',
    age: 45,
    mas: [
        {id: 1,text: '344343'},
        {id: 1,text: '344343'},
        {id: 1,text: '344343'}
    ],
    dream: {
        teachers:{
            name: 'mike',
            age: '32'
        }
    }
}
let b = {...a}
b.mas = {...a.mas}
b.mas[0] = {...a.mas[0]}
b.mas[2] = {...a.mas[1]}
b.mas[1] = {...a.mas[2]}
b.dream = {...a.dream}
b.dream.teachers = {...a.dream.teachers}
b.dream.teachers.name = 'enf'
 a.mas.push({id:9,text:'6666666'})
b.name = '55555555'
console.log(b.mas)
console.log(a.mas)
//console.log(a.mas)

let mapStateToProps = (state) => {
    return {
        newPostText: state.ProfilePage.newPostText,
        posts: state.ProfilePage.PostData
    }
}
let mapDispatchToProps = (dispatch)=> {
    return {
        onPostChange: (text)=> {
                   dispatch(ActionCreatorUpdatePostText(text));
                    },
        onaddDialogs: ()=>{

                dispatch(ActionCreatorAddPost());

        }
    }
}
const MyPostContainer = connect(mapStateToProps,mapDispatchToProps)(MyPost)


export default MyPostContainer;
