import React from 'react'
import s from "./Users.module.css";
import userPhoto from "../../img/rik.jpg";
import {NavLink} from "react-router-dom";
import axios from "axios";
import {UserApi} from "../../api/api";
import {TogleFollow} from "../../Readux/Reducer-Users";

let Users = (props) => {
    let page = []
    let colPages = Math.ceil(props.TotalCount / props.pagesize)
    for (let i = 1; i <= 20; i++) {
        page.push(i)
    }
    return <div>
        <div>
            {page.map(p =>
                <span onClick={() => props.SetUserPage(p)}
                      className={props.currentPage == p && s.active}>{p}</span>
            )}

        </div>
        {
            props.Users.map(u => <div key={u.id}>
                <div className={s.main}>
                    <div className={s.users}>
                        <NavLink to={'/profile/' + u.id}>
                            <img className={s.usersPhoto} src={u.photos.small != null ? u.photos.small : userPhoto}/>
                        </NavLink>
                        <div className={s.button}>
                            {u.followed ? <button disabled={props.togglefollow} onClick={() => {
console.log(props)
                                props.TogleFollow(u.id)
                                UserApi.deletetUserFollow(u.id).then(data => {
                                    if (data.resultCode === 0)
                                        props.UnFollow(u.id)
                                    props.TogleFollow(false)
                                })

                            }}>Unfollow</button> : <button disabled={props.togglefollow} onClick={() => {
                                props.TogleFollow(u.id)
                                UserApi.postUserFollow(u.id).then(data => {
                                    if (data.resultCode === 0)
                                        props.OnFollow(u.id)
                                    props.TogleFollow(false)
                                })

                            }}>follow</button>}
                        </div>
                    </div>
                    <div className={s.item}>
                        <div className={s.one}>{u.name}</div>
                        <div className={s.two}>}</div>
                        <div className={s.three}></div>
                        <div className={s.foor}></div>
                    </div>
                </div>
            </div>)
        }
    </div>

}
export default Users
