import React from 'react';
import { connect } from 'react-redux';
import {ActionCreatorAddPost, ActionCreatorUpdatePostText} from "../../Readux/Reducer-Profile";
import Profile from "./Profile";

let myPostToprops = (state)=>{
    return{
        newPostText: state.Profile.newPostText,
        posts: state.Profile.PostData

    }
}
let myPostToDispatch = (dispatch)=>{
    return{
        onPostChange: (text)=>{
            dispatch(ActionCreatorUpdatePostText(text))
        },
        onaddPost: ()=>{
            dispatch(ActionCreatorAddPost)
        }
    }

}
const ProfileContainer = connect(myPostToprops,myPostToDispatch)(Profile)
export default ProfileContainer
