import React from 'react';

class ProfileStatus extends React.Component {
    statusRef = React.createRef()
    state = {
        editmode: false
    }
activateEditMode =()=>{
       this.setState({
           editmode: true
       })
}
deactivateEditMode =()=>{
       this.setState({
           editmode: false
       })
    this.props.NewProfileStatusThunk(this.statusRef.current.value)
}
    setStutus = (e) => {
        let text = e.target.value
        console.log(text)
        this.props.NewProfileStatusThunk(text)
    }
    render() {
        return <div>
            {!this.state.editmode &&
                <div><span onDoubleClick={this.activateEditMode}>{this.props.profilestatus}</span></div>
            }
            {this.state.editmode &&
                <div>
                    <input ref={this.statusRef} autoFocus={true} onBlur={this.deactivateEditMode} type="text"  value={this.props.profilestatus} />
                </div>
            }
        </div>
    }
}

export default ProfileStatus;
