import axios from "axios";

export  const getUsersApi = (currentPage= 1,pagesize= 10) => {
return     axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pagesize}`, {
        withCredentials: true
    })

}
