import React from 'react';
import {connect} from "react-redux";
import {FollowUserAC, SetUsersAC, SetPagesAC, UnFollowUserAC} from "../../Readux/Reducer-Users";
import axios from "axios";
import Users from "./Users";

class UsersAPIComponent extends React.Component {


    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pagesize}`).then(response => {
            this.props.SetUsers(response.data.items)
            console.log(response.data.items)

        })
    }

    SetUserPage = (p) => {
        this.props.SetPage(p)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=10`).then(response => {
            this.props.SetUsers(response.data.items)


        })
    }

    render() {
        return <Users SetUserPage={this.SetUserPage}
                      Users={this.props.Users}
                      TotalCount={this.props.TotalCount}
                      pagesize={this.props.pagesize}
                      UnFollow={this.props.UnFollow}
                      OnFollow={this.props.OnFollow}
                      currentPage={this.props.currentPage}/>
    }
}

let mapStateToUsers = (state) => {
    return {
        Users: state.usersPage.users,
        TotalCount: state.usersPage.TotalCount,
        pagesize: state.usersPage.PageSize,
        currentPage: state.usersPage.currentPage
    }
}

let initMapDispatchToProps = (dispatch) => {
    return {
        OnFollow: (userid) => {
            dispatch(FollowUserAC(userid))
        },
        UnFollow: (userid) => {
            dispatch(UnFollowUserAC(userid))
        },
        SetUsers: (users) => {
            dispatch(SetUsersAC(users))
        },
        SetPage: (page) => {
            dispatch(SetPagesAC(page))
        }
    }
}

const MyUsersContainer = connect(mapStateToUsers, initMapDispatchToProps)(UsersAPIComponent)


export default MyUsersContainer;
