let renderState = (state)=> {
    ReactDOM.render(
        <StoreConrexr.Proveder value={store}>
        <App state={state} store={store} dispatch={store.dispatch.bind(store)} />,
        document.getElementById('root')
        </StoreConrexr.Proveder>
