import {Login, usersAPI} from "../api/api";

const SETAUTH = 'SETAUTH'

let initstate = {
    userid: '',
    login: '',
    email: '',
    isAuth: false

}
const reducerAuth = (state = initstate, action) => {
    switch (action.type) {
        case SETAUTH:
           return {
               ...state,
               ...action.payload,
           }
        default:
            return state
    }
}
export const SetAuth = (id,login,email,isAuth) => ({type: SETAUTH,payload:{id,login,email,isAuth}})
export const AuthThunk = (dispatch)=> {
    return (dispatch)=> {
        usersAPI.apiAuth().then(response => {
            if (response.data.resultCode === 0) {
                let {id,login,email} = response.data.data
                dispatch(SetAuth(id,login,email,true))
           }
    })
}
}
export const LoginThunk = (email,password,rememberme)=> {
    return (dispatch)=> {
        Login.login(email,password,rememberme).then(response => {
            if (response.data.resultCode === 0) {
                dispatch(AuthThunk())
            }
        })
    }
}
export const UnLoginThunk = ()=> {
    return (dispatch)=> {
        debugger
        Login.deletelogin().then(response => {
            if (response.data.resultCode === 0) {
                dispatch(SetAuth(null,null,null.false))
            }
        })
    }
}

export default reducerAuth
