import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import state from './Readux/State'
import {addPost,PostTextChange,subscribe} from "./Readux/State";
  let rerenderState = (state) =>{
 ReactDOM.render(
     <App state={state} addPost={state.addPost} PostTextChange={state.PostTextChange} />,
     document.getElementById('root')
 )
}
rerenderState(state)
