import React from 'react';
import {connect} from "react-redux";
import Users from "./Users";
import {FollowUserAC, UnFollowUserAC} from "../../Readux/Reducer-Users";

let mapStateToUsers = (state) => {
    return {
        PostData: state.users.PostData,
        nameButton: state.users.nameButton
    }
}

let initMapDispatchToProps = (dispatch) =>{
    return {
        OnFollow : (userid)=>{
            dispatch(FollowUserAC(userid))
        },
        UnFollow : (userid)=>{
            dispatch(UnFollowUserAC(userid))
        }
    }
}
const MyUsersContainer = connect(mapStateToUsers,initMapDispatchToProps)(Users)


export default MyUsersContainer;
