import React from 'react';
import Profile from "./Profile";
import axios from "axios";
import {connect} from "react-redux";
import {
    getProfileStatusThunk,
    NewProfileStatusThunk,
    ProfileThunk
} from "../../Readux/Reducer-Profile";
import {Navigate, useParams} from "react-router-dom";
import {WithAuthContainer} from "../../HOC/AuthHOC";
import {compose} from "redux";


const withRouter = WrappedComponent => props => {
    const params = useParams();
    return (
        <WrappedComponent
            {...props}
            params={params}
        />
    );
};

class ProfileContainer extends React.Component {
    componentDidMount() {

        let userid = this.props.params.userId
        if(!userid){
            userid = 2
        }
        this.props.ProfileThunk(userid)
        this.props.getProfileStatusThunk(userid)
    }


    render() {
        return <div>
            <Profile {...this.props} profile={this.props.profile} NewProfileStatusThunk={this.props.NewProfileStatusThunk} />
        </div>
    }
}

let RedirectComponent = WithAuthContainer(ProfileContainer)

let mapStateToProps = (state) => ({
    profile: state.ProfilePage.profile,
    profilestatus: state.ProfilePage.status

})

const ProfileContainerwithRouter = withRouter(RedirectComponent)
export default compose(connect(mapStateToProps, {ProfileThunk,getProfileStatusThunk,NewProfileStatusThunk}),
    withRouter,
    WithAuthContainer
)(ProfileContainerwithRouter)
