import React from 'react';
import axios from "axios";


let User = (props)=>{
    if(props.users===0){
axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response=>{
    props.SetUsers(response.data.users)
})
    }
    return <div>
        {
props.users.map(u=>(
    <div>
        <div>{u.name}</div>
        {u.followed ===true ? <button onClick={()=>props.onFollow(u.id)}>unfollow</button> : <button onClick={()=>props.Follow(u.id)}>follow</button> }
    </div>
))
        }
    </div>
}
export default User
