import React from 'react'
import { reduxForm} from "redux-form";
import {connect} from "react-redux";
import {LoginThunk} from "../../Readux/Reducer-Auth";
import {createField, Input} from "../Common/FormControls/FormControls";
import {maxleight, required} from "../../utils/validators/validators";
import {Navigate} from 'react-router-dom';

const maxleight20 = maxleight(30)
const LogiForm = (props) => {
    console.log(props)
    return (
        <form onSubmit={props.handleSubmit}>
            {createField('text', 'email', Input, [required, maxleight20], 'login',null,null)}
            {createField('password', 'password', Input, [required, maxleight20], 'password',null,null)}
            {createField('checkbox', 'rememberMe', Input, null, null,null,'remember me')}

            <div>
                <button>Login</button>
            </div>

        </form>
    )
}
const LoginFormRedux = reduxForm({
    form: 'login', // имя формы в state (state.form.login)
})(LogiForm)

const Loginpage = (props) => {
    const onSubmit = (formData) => {
        props.LoginThunk(formData.email, formData.password, formData.rememberMe)
    }
    if (props.isAuth) {
        return <Navigate to={"/profile"}/>
    }
    return <div>
        <h1>LOGIN</h1>
        <LoginFormRedux onSubmit={onSubmit}/>
    </div>
}
let mapStateToLogin = (state) => {
    return {
        isAuth: state.Auth.isAuth,
    }
}

export default connect(mapStateToLogin, {LoginThunk})(Loginpage)
