import React from 'react';
import Post from "./Post/Post";
import {Field, reduxForm} from "redux-form";
import {maxLength15, minLength2,  required} from "../../../utils/validators";
import {renderField} from "../../FormControls/FormsControl";



window.props=[]

class MyPost extends React.Component {
    componentDidUpdate(prevProps, prevState, snapshot) {
        setTimeout(()=>{
            this.setState()
        },300)
    }

    shouldComponentUpdate(nextProps, nextState) {
        return nextProps!=this.props && nextState!=this.props
    }

    render() {
        console.log('render')
        window.props.push(this.props)
        let PostMass = this.props.posts.map(p => <Post message={p.text} key={p.id} like={p.like}/>)
        let addPost = (value) => {
            this.props.onaddDialogs(value.profile)
        }


        return (
            <div>
                <div>
                    <ProfileFormRedux onSubmit={addPost}/>
                </div>
                {PostMass}
            </div>
        );
    }
}


const ProfileForm=(props)=>{
    const {handleSubmit}=props
    return (
        <form onSubmit={handleSubmit} >
            <div>
                <Field placeholder='Enter your message' name='profile'  component={renderField} type='text' validate={[ maxLength15, minLength2]} />
            </div>
            <button type="submit">Submit</button>

        </form>
    )
}
export default MyPost;
let ProfileFormRedux = reduxForm({
    // a unique name for the form
    form: 'profileform'
})(ProfileForm)
