import React from 'react';
import Header from "./Header";
import {connect} from "react-redux";
import {AuthThunk, SetAuth, UnLoginThunk} from "../../Readux/Reducer-Auth";
class HeaderContainer extends React.Component {
    componentDidMount() {


        this.props.AuthThunk()

        }


    render(){
        return        <Header {...this.props} UnLoginThunk={this.UnLoginThunk} />
    }
}
let mapStateToProps = (state)=>{
    return{
        isAuth: state.Auth.isAuth,
        login: state.Auth.login


    }
}
export default connect (mapStateToProps,{AuthThunk,UnLoginThunk})(HeaderContainer)
