import React from 'react';
export default class ProfileStratus extends React.Component{

    state={
        editmode: false
    }
    activatedStatusMode=()=>{
    this.setState({
                 editmode: true
             })
}
deactivateStatusMode=()=>{
        this.setState({
            editmode: false
        })
}
        render(){
           // console.log(this.props.profilestatus)
        return <div>
            {!this.state.editmode && <div><span onClick={this.activatedStatusMode}>c{this.props.profilestatus}</span> </div>   }
            {this.state.editmode && <div><input autoFocus={true} onBlur={this.deactivateStatusMode} type="text"/> </div>   }
        </div>
        }

}
