import axios from "axios";
  const setUsersApi =(currentPage,pagesize=5)=>{
    axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pagesize}`,{
        withCredentials: true
    })
}
export default setUsersApi()
