import React from 'react'
import s from "./Users.module.css";
import userPhoto from "../../img/rik.jpg";
import {NavLink} from "react-router-dom";
import axios from "axios";
import {TogleProgressFetching} from "../../Readux/Reducer-Users";
import {usersAPI} from "../../api/getUsersApi";

let Users = (props) => {
    console.log(props.isfetchingprogress)
    console.log('props.isfetchingprogress')
    let page = []
    let colPages = Math.ceil(props.TotalCount / props.pagesize)
    for (let i = 1; i <= 20; i++) {
        page.push(i)
    }
    return <div>
        <div>
            {page.map(p =>
               <span onClick={() => props.SetUserPage(p)}
                             className={props.currentPage == p && s.active}>{p}</span>

            )}

        </div>
        {
            props.Users.map(u => <div key={u.id}>
                <div className={s.main}>
                    <div className={s.users}>
                        <NavLink to={'/profile/' + u.id}>
                        <img className={s.usersPhoto} src={u.photos.small != null ? u.photos.small : userPhoto}/>
                        </NavLink>
                            <div className={s.button}>
                            {u.followed ? <button disabled={props.isfetchingprogress} onClick={() => {
                                props.TogleProgressFetching(true)
                                usersAPI.getFollowUsersApi(u.id).then(response => {
                                    if(response.data.resultCode == 0) {
                                        props.UnFollow(u.id)
                                    }

                                    props.TogleProgressFetching(false)
                                })

                            }}>unfollow</button> : <button disabled={props.isfetchingprogress} onClick={() => {
                                props.TogleProgressFetching(true)
                                axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{ },{
                                    withCredentials: true,
                                    headers: {
                                        'API-KEY' : '86c79010-dccf-4af0-abdb-c44354c00af2'
                                    }
                                }).then(response => {
                                    if(response.data.resultCode == 0) {
                                        props.UnFollow(u.id)
                                    }
                                    props.TogleProgressFetching(false)
                                })
                            }}>follow</button>}
                        </div>
                    </div>
                    <div className={s.item}>
                        <div className={s.one}>{u.name}</div>
                        <div className={s.two}>}</div>
                        <div className={s.three}></div>
                        <div className={s.foor}></div>
                    </div>
                </div>
            </div>)
        }
    </div>

}
export default Users
