import React from 'react';
import Post from "./Post/Post";
import {Field, reduxForm} from "redux-form";



const required = value => (value || typeof value === 'number' ? undefined : 'Required')
const maxLength = max => value =>
    value && value.length > max ? `Должно быть не больше ${max} букв` : undefined
const maxLength15 = maxLength(15)
export const minLength = min => value =>
    value && value.length < min ? `Должно быть не менее ${min} букв` : undefined
export const minLength2 = minLength(2)



const renderField = ({
                         input,
                         label,
                         type,
                         meta: { touched, error, warning }
                     }) => (
    <div>
        <label>{label}</label>
        <div>
            <input {...input}  />
            {touched &&
                ((error && <span>{error}</span>) ||
                    (warning && <span>{warning}</span>))}
        </div>
    </div>
)

const MyPost = (props) => {

    let PostMass = props.posts.map(p => <Post message={p.text} key={p.id} like={p.like}/>)
    let addPost = (value) => {
        props.onaddDialogs(value.profile)
    }

    return (
        <div>
            <div>
               <ProfileFormRedux onSubmit={addPost} />
            </div>
            {PostMass}
        </div>
    );
}


const ProfileForm=(props)=>{
    const {handleSubmit}=props
    return (
        <form onSubmit={handleSubmit} >
            <div>
                <Field placeholder='Enter your message' name='profile'  component={renderField} type='text' validate={[required, maxLength15, minLength2]} />
            </div>
            <button type="submit">Submit</button>

        </form>
    )
}
export default MyPost;
let ProfileFormRedux = reduxForm({
    // a unique name for the form
    form: 'profileform'
})(ProfileForm)
