const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'

let initstate = {
    users: [
        {id: 1, followed:true, name: 'Dmitry', location:{country: 'China',city: 'Saratov'} ,message: 'it kamasutra'},
        {id: 1, followed:true, name: 'Dmitry', location:{country: 'China',city: 'Saratov'} ,message: 'it kamasutra'},
        {id: 1, followed:true, name: 'Dmitry', location:{country: 'China',city: 'Saratov'} ,message: 'it kamasutra'},
        {id: 1, followed:true, name: 'Dmitry', location:{country: 'China',city: 'Saratov'} ,message: 'it kamasutra'}

    ],
    nameButton: 'It kamasutra'
}
const reducerUsers = (state = initstate, action) => {

    switch (action.type) {
        case FOLLOW :
            return {
        ...state,
       users: state.users.map(u=>{
           if (u.id === action.userid){
               return {...u,followed: true}
           }
               return u
       })
            }
        case UNFOLLOW :
            return {
                ...state,
                users: state.users.map(u=>{
                    if (u.id === action.userid){
                        return {...u,followed: false}
                    }
                    return u
                })
            }
        default:
            return state
    }

}
export const ActionCreatorAddPost = (userid) => ({type: FOLLOW,userid})
export const ActionCreatorUpdatePostText = (userid) => ({type: UNFOLLOW, userid})
export default reducerUsers
