import React from 'react';
import {UpdateProfileThunkStatus} from "../../../Readux/Reducer-Profile";
export default class ProfileStratus extends React.Component{

    state={
        status: this.props.profilestatus,
        editmode: false
    }
    activatedStatusMode=()=>{
    this.setState({
                 editmode: true
             })
}
deactivateStatusMode=()=>{
        this.setState({
            editmode: false
        })
    //props.UpdateProfileThunkStatus
}
addLocalStatus=(event)=>{
    //event.target.value = this.state._state+event.target.value
    this.props.UpdateProfileThunkStatus(event.target.value)
    console.log(event.target.value);
}
componentDidUpdate() {
        if(prev.props.profilestatus!=this.props.profilestatus){
            this.setState({
                status: this.props.profilestatus
            })
        }
}

    render(){
            console.log(this.props)
        return <div>
            {!this.state.editmode && <div><span onClick={this.activatedStatusMode}>{this.state.status}</span> </div>   }
            {this.state.editmode && <div><input autoFocus={true} onChange={this.addLocalStatus} onBlur={this.deactivateStatusMode} value={this.props.profilestatus} type="text"/> </div>   }
        </div>
        }

}
