import React, {useState} from 'react';

 const ProfileStratus =(props)=> {
let  [editmode,Seteditmode]= useState(false)
let  [status,Setstatus]= useState(props.profilestatus)
     const activatedStatusMode = () => {
         Seteditmode(true)
     }
     const deactivateStatusMode = () => {
         Seteditmode(false)
         //         this.props.UpdateProfileThunkStatus(this.state.status)
     }
     const onStatusChange = (e) => {
         Setstatus( e.currentTarget.value)
     }
        return <div>
            {!editmode && <div><span onClick={activatedStatusMode}
                >{props.profilestatus}</span>
            </div>}
            {editmode &&
                <div><input onChange={onStatusChange} autoFocus={true} value={props.profilestatus} onBlur={deactivateStatusMode}  type="text"/></div>}
        </div>
}
export default ProfileStratus
