import axios from "axios";

const instance = axios.create({
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    withCredentials: true,
    headers: { 'API-KEY': '7a884532-82d5-4d4e-b1f5-1ce8ea45b596'}
});
/*
export const getUsers=(currentPage,pagesize)=>{
    return await instance.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pagesize}`,{
        withCredentials: true
    }).then(response=>{
        return response.data
    })
}

axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`
                                    ,{},{
                                        withCredentials: true,
                                        headers: {
                                            'API-KEY': '7a884532-82d5-4d4e-b1f5-1ce8ea45b596'
                                        }
                                    }
                                )
 */
export const UserApi={
    getUserApi(currentPage,pagesize){
        return instance.get(`users?page=${currentPage}&count=${pagesize}`).then(response=>{
            return response.data
        })
    } ,
    getUserPostFollow(id){
        return instance.post(`follow/${id}`).then(response=>{
            return response.data
        })
    }
}
